import React, { useContext } from "react";

import { cartDropDownContext } from "../../contexts/cart-dropdown.context";
import CartItem from "..//cart-item/cart-item.component";
import "../checkout/checkout.styles.scss";

const CheckOut = () => {
  const { cartItems, delItem } = useContext(cartDropDownContext);

  return (
    <div className="checkout-main">
      <div className="line-descriptions">
        <span>Product</span>
        <span>Description</span>
        <span>Quantity</span>
        <span>Price</span>
        <span>Revmove</span>
      </div>
      <hr />
      {cartItems.map((item) => {
        return (
          <div>
            <div className="check-out-item" key={item.id}>
              <img className="item-img" src={item.imageUrl} />
              <span className="item-name">{item.name}</span>
              <span className="item-quantity">{item.quantity}</span>
              <span className="item-price">{item.price}</span>
              <button className="button-delete-item">X</button>
            </div>
            <hr />
          </div>
        );
      })}
    </div>
  );
};

export default CheckOut;
